-- ----------------------------
-- Table structure for app_agent_relation
-- ----------------------------
DROP TABLE IF EXISTS `app_agent_relation`;
CREATE TABLE `app_agent_relation` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `t1_id` bigint NOT NULL DEFAULT '-1' COMMENT '一级代理ID',
  `t2_id` bigint NOT NULL DEFAULT '-1' COMMENT '二级代理ID',
  `t3_id` bigint DEFAULT NULL COMMENT '三级代理ID',
  `top_share_code` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '总代用户邀请码',
  `level` int NOT NULL DEFAULT '0' COMMENT '级别: (0)-总代 (1)-一级代理 (2)-二级代理 (3-无限)-会员 ',
  `user_group` int NOT NULL DEFAULT '1' COMMENT '用户组 1正式组 0测试组',
  `ori_user_id` bigint NOT NULL COMMENT '(源)用户ID',
  `ori_share_code` varchar(36) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '(源)用户邀请码',
  `ori_account` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '(源)用户账号',
  `p1_id` bigint NOT NULL DEFAULT '-1' COMMENT '一级代理用户id(直属上级)',
  `p1_code` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '一级代理邀请码(直属上级)',
  `p1_account` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '一级代理账号(直属上级)',
  `p2_id` bigint NOT NULL DEFAULT '-1' COMMENT '二级代理用户id(直属上级)',
  `p2_code` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '二级代理邀请码(直属上级)',
  `p2_account` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '二级代理账号(直属上级)',
  `p3_id` bigint NOT NULL DEFAULT '-1' COMMENT '三级代理用户id(直属上级)',
  `p3_code` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '三级代理邀请码(直属上级)',
  `p3_account` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '三级代理账号(直属上级)',
  `p4_id` bigint DEFAULT NULL,
  `p5_id` bigint DEFAULT NULL,
  `p6_id` bigint DEFAULT NULL,
  `p7_id` bigint DEFAULT NULL,
  `p8_id` bigint DEFAULT NULL,
  `p9_id` bigint DEFAULT NULL,
  `create_by` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '创建人',
  `create_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_by` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '更新人',
  `update_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `id_topUserId` (`t1_id`) USING BTREE COMMENT '总代邀请码-索引',
  KEY `id_ori` (`ori_user_id`) USING BTREE COMMENT '源索引',
  KEY `id_p1` (`p1_id`) USING BTREE COMMENT '一级邀请码-索引'
) ENGINE=InnoDB AUTO_INCREMENT=188 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='代理层级关联表';

