-- ----------------------------
-- Table structure for app_cert_request
-- ----------------------------
DROP TABLE IF EXISTS `app_cert_request`;
CREATE TABLE `app_cert_request` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT 'id',
  `cert_level` int NOT NULL DEFAULT '1' COMMENT '认证等级申请: 1：初级认证 2:高级认证',
  `user_id` bigint NOT NULL COMMENT '会员id',
  `uid` bigint NOT NULL DEFAULT '-1' COMMENT 'uid',
  `t1_id` bigint DEFAULT '-1' COMMENT '一级代理id',
  `t2_id` bigint DEFAULT '-1' COMMENT '二级代理ID',
  `t3_id` bigint DEFAULT NULL COMMENT '三级代理id',
  `user_name` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '会员名称',
  `real_name` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '真实姓名',
  `email` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '邮箱',
  `id_card_front` varchar(500) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '身份证证面',
  `id_card_back` varchar(500) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '身份证背面',
  `idNumber` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '身份证号',
  `address` varchar(500) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '住址',
  `id_card_in_hand` varchar(500) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '手持身份证',
  `reject_reason` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '失败原因,如果有',
  `status` int NOT NULL DEFAULT '0' COMMENT '审核状态 0:待审核 1:成功 -1:失败',
  `opt_user` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '操作人',
  `remark` varchar(225) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '备注',
  `create_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='认证申请';

