-- ----------------------------
-- Table structure for app_exchange_cross_order
-- ----------------------------
DROP TABLE IF EXISTS `app_exchange_cross_order`;
CREATE TABLE `app_exchange_cross_order` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT 'id',
  `business_type` int NOT NULL DEFAULT '0' COMMENT '业务类型 （0：空，1：多）',
  `trade_type` int NOT NULL DEFAULT '0' COMMENT '交易类型 0:限价交易 1:市价交易',
  `user_id` bigint NOT NULL COMMENT '会员id',
  `uid` bigint NOT NULL DEFAULT '-1' COMMENT 'uid',
  `user_name` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '会员名称',
  `user_group` int DEFAULT NULL COMMENT '正常 0 假人 1 机器人 2',
  `top_user_id` bigint DEFAULT '-1' COMMENT '总代用户ID',
  `t2_id` bigint DEFAULT '-1' COMMENT '二级代理ID',
  `t3_id` bigint DEFAULT NULL COMMENT '三级代理id',
  `p1_account` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '上级账号',
  `order_number` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '订单编号',
  `symbol` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '交易对 BTC/USDT  USDT是计价货币(quote) BTC是交易资产(base)',
  `lever_code` varchar(32) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '1X' COMMENT '杠杆倍率',
  `lever` int NOT NULL DEFAULT '1' COMMENT '杠杆倍率(计算)',
  `open_price` decimal(20,8) DEFAULT NULL COMMENT '开仓价',
  `trigger_value` decimal(20,8) DEFAULT NULL COMMENT '触发价-止盈',
  `lose_value` decimal(20,8) DEFAULT NULL COMMENT '触发价-止损',
  `mark_price` decimal(20,8) DEFAULT NULL COMMENT '标记价(平仓价)',
  `quit_price` decimal(20,8) DEFAULT NULL COMMENT '爆仓线(逻辑字段,不展示)',
  `focus_amount` decimal(20,8) DEFAULT '0.00000000' COMMENT '超出部分保证金(逻辑字段,不展示)',
  `over_price` decimal(20,8) DEFAULT NULL COMMENT '盈亏价,表示保证金的盈亏价格(逻辑字段,不展示)',
  `freeze` bit(1) NOT NULL DEFAULT b'0' COMMENT '锁仓(爆仓判定,逻辑字段不展示)',
  `quantity` decimal(20,8) DEFAULT '0.00000000' COMMENT '持仓数量',
  `amount` decimal(20,8) DEFAULT NULL COMMENT '保证金',
  `balance` decimal(20,8) DEFAULT NULL COMMENT '购买时余额',
  `balance_percentage` decimal(5,2) DEFAULT NULL COMMENT '仓位',
  `fee` decimal(20,8) DEFAULT NULL COMMENT '手续费',
  `cross_amount` decimal(20,8) DEFAULT NULL COMMENT '合约金额',
  `roe` decimal(20,4) DEFAULT '0.0000' COMMENT '盈亏百分比(保留小数点4位)',
  `in_come` decimal(20,4) DEFAULT '0.0000' COMMENT '盈亏',
  `status` int NOT NULL DEFAULT '0' COMMENT '订单状态 （-1:撤单 0:开仓委托 1:开仓成交 2:平仓委托 3:平仓成交）',
  `create_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间，下单时间',
  `settlement_time` timestamp NULL DEFAULT NULL COMMENT '结算时间(平仓时间)',
  `update_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `ts` bigint NOT NULL DEFAULT '-1' COMMENT '时间戳',
  `t1_id` bigint DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `uq_order_number` (`order_number`) USING BTREE COMMENT '订单号唯一键',
  KEY `symbol` (`symbol`,`user_id`,`status`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=576 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='永续合约(伪)订单';

