  `max_leverage` int NOT NULL DEFAULT '125' COMMENT '最大杠杆',
  `leverage_brackets` json NOT NULL COMMENT '杠杆档位 JSON 配置',
  `maker_fee` decimal(10,8) NOT NULL DEFAULT '0.00020000' COMMENT '挂单手续费',
  `taker_fee` decimal(10,8) NOT NULL DEFAULT '0.00060000' COMMENT '吃单手续费',
  `funding_interval` int NOT NULL DEFAULT '8' COMMENT '资金费率周期（小时）',
  `funding_rate_cap` decimal(10,8) NOT NULL DEFAULT '0.00300000' COMMENT '资金费率上限',
  `funding_rate_floor` decimal(10,8) NOT NULL DEFAULT '-0.00300000' COMMENT '资金费率下限',
