  `quote_symbol` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '计价货币(quote)',
  `direction` int NOT NULL DEFAULT '0' COMMENT '订单成交方向 （0：买，1：卖）',
  `trade_type` int NOT NULL DEFAULT '0' COMMENT '交易类型 0:限价交易 1:市价交易',
  `buyer_order_id` bigint NOT NULL COMMENT '买方订单id',
  `seller_order_id` bigint NOT NULL COMMENT '卖方订单id',
  `buyer_id` bigint NOT NULL COMMENT '买方id',
  `buyer_name` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '买方会员名称',
  `seller_id` bigint NOT NULL COMMENT '卖方id',
  `seller_name` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '卖方会员名称',
  `buyer_user_group` int DEFAULT NULL COMMENT '正常 0 假人 1 机器人 2',
  `buyer_top_user_id` bigint DEFAULT '-1' COMMENT '买方总代用户ID',
  `seller_user_group` int DEFAULT '0' COMMENT '正常 0 假人 1 机器人 2',
  `seller_top_user_id` bigint DEFAULT '-1' COMMENT '卖方总代用户ID',
  `buyer_price` decimal(20,8) DEFAULT NULL COMMENT '买方报价',
  `seller_price` decimal(20,8) DEFAULT NULL COMMENT '卖方报价',
  `quote_amount` decimal(20,8) DEFAULT NULL COMMENT '买方应扣资产',
  `base_amount` decimal(20,8) DEFAULT NULL COMMENT '卖方应扣资产',
  `matched` decimal(20,8) DEFAULT NULL COMMENT '成交量',
  `unfreeze_quote` decimal(20,8) DEFAULT NULL COMMENT '买方额外未使用的报价',
  `buyer_fee_rate` decimal(8,4) DEFAULT '0.0000' COMMENT '买方手续费率',
  `seller_fee_rate` decimal(8,4) DEFAULT '0.0000' COMMENT '买方手续费率',
  `buyer_fee` decimal(20,8) DEFAULT NULL COMMENT '买方本次扣除手续费',
  `seller_fee` decimal(20,8) DEFAULT NULL COMMENT '卖方本次扣除手续费',
  `status` int NOT NULL DEFAULT '0' COMMENT '状态 （0：等待结算，1:已结算）',
  `op_note` varchar(500) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT NULL COMMENT '操作说明',
  `business_type` int DEFAULT '0' COMMENT '业务类型 0：普通业务 1：体验金订单',
  `rebate_note` varchar(500) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT NULL COMMENT '返佣说明',
  `create_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间，下单时间',
  `settlement_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '结算时间',
  `update_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `symbol` (`symbol`,`status`) USING BTREE,
  KEY `status` (`status`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=48 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='币币交易撮合记录';

