  `unfilled_quantity` decimal(20,8) DEFAULT NULL COMMENT '尚未成交的数量',
  `status` int NOT NULL DEFAULT '0' COMMENT '订单状态 （0：等待成交，1：部分成交 2:完全成交， -1：部分取消 -2: 完全取消）',
  `fee_rate` decimal(8,4) DEFAULT '0.0000' COMMENT '手续费率',
  `fee` decimal(20,8) DEFAULT NULL COMMENT '总应扣手续费',
  `freeze_quote` decimal(20,8) DEFAULT NULL COMMENT '已冻结资产 (例如 BTC/USDT), 买方则冻结USDT 卖方冻结BTC',
  `business_type` int DEFAULT '0' COMMENT '业务类型 0：普通业务 1：体验金订单',
  `create_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间，下单时间',
  `expire_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '到期时间',
  `settlement_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '结算时间',
  `update_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `can_show` bit(1) DEFAULT b'1' COMMENT '是否显示，默认true',
  `can_sell` bit(1) DEFAULT b'1' COMMENT '是否可出售,默认true',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `uq_order_number` (`order_number`) USING BTREE COMMENT '订单号唯一键',
  KEY `symbol` (`symbol`,`user_id`,`status`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=131 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='币币交易挂单';

