  UNIQUE KEY `uq_symbol` (`symbol`) USING BTREE COMMENT '交易对唯一约束'
) ENGINE=InnoDB AUTO_INCREMENT=249 DEFAULT CHARSET=utf8mb3 COMMENT='币币交易对';

