-- ----------------------------
-- Table structure for app_ieo
-- ----------------------------
DROP TABLE IF EXISTS `app_ieo`;
CREATE TABLE `app_ieo` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT 'id',
  `project_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '项目名称',
  `type` int DEFAULT '0' COMMENT '类型： 0-新币认购 1-平台币申请',
  `symbol` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '交易对 BTC/USDT  USDT是计价货币(quote) BTC是交易资产(base)',
  `base_symbol` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '交易资产(base)',
  `quote_symbol` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '计价货币(quote)',
  `publish_price` decimal(20,4) NOT NULL DEFAULT '0.0000' COMMENT '发行价格',
  `publish_total` bigint NOT NULL DEFAULT '0' COMMENT '发行量',
  `buy_total` bigint NOT NULL DEFAULT '0' COMMENT '已购买量',
  `remaining_progress` decimal(5,2) NOT NULL DEFAULT '0.00' COMMENT '剩余进度',
  `buy_limit_max` bigint NOT NULL DEFAULT '0' COMMENT '最大购买量',
  `buy_limit_min` bigint NOT NULL DEFAULT '0' COMMENT '最小购买量',
  `buy_max_times` int DEFAULT '0' COMMENT '最大购买次数(>0生效)',
  `ad_time_start` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '公告开始时间（筹集开始时间即是公告结算时间）',
  `sell_time_start` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '筹集开始时间',
  `sell_time_end` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '筹集结束时间',
  `issued_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发行时间(发行时间必须晚于筹集结束时间，若不填，筹集结束时间即发行时间)',
  `status` int DEFAULT '0' COMMENT '发行状态 0：未发行 1：已发行',
  `remark` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '备注',
  `white_paper` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '白皮书',
  `is_show` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否展示',
  `create_by` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '创建人',
  `deleted` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否删除',
  `create_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_by` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '更新人',
  `update_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=40 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='新币申购';

