CREATE TABLE `app_level` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT 'id',
  `level_name` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '等级名称',
  `level_weights` int NOT NULL DEFAULT '0' COMMENT '等级权重，高向低兼容',
  `source_host` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '资源服务器域名',
  `logo` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'logo',
  `chat_times` int NOT NULL DEFAULT '0' COMMENT '提问次数/天',
  `max_task` int NOT NULL DEFAULT '0' COMMENT '投资次数/天',
  `commission_rate` decimal(8,4) DEFAULT '0.0000' COMMENT '平台佣金比例',
  `content` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci COMMENT '描述',
  `enable` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否启用',
  `create_by` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '创建人',
