-- ----------------------------
DROP TABLE IF EXISTS `app_msg_message`;
CREATE TABLE `app_msg_message` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '消息主键ID，自增',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '消息标题，最大128字符',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '消息内容，支持长文本',
  `type` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '消息类型：SYSTEM(系统消息)/ORDER(订单消息)/SECURITY(安全消息)/MARKET(市场消息)/PROMOTION(推广消息)',
  `priority` tinyint NOT NULL COMMENT '消息优先级：1=高优先级(紧急重要)/2=中优先级(一般重要)/3=低优先级(普通信息)',
  `status` varchar(16) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '消息状态：DRAFT(草稿)/SENT(已发送)/ARCHIVED(已归档)',
  `extra` json DEFAULT NULL COMMENT '扩展字段，JSON格式存储业务相关数据，如订单ID、跳转链接、业务参数等',
  `created_by` bigint NOT NULL COMMENT '消息创建者ID，关联管理员表',
  `sent_by` bigint DEFAULT NULL COMMENT '消息发送操作者ID，关联管理员表，可能不同于创建者',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '消息创建时间',
  `sent_at` datetime DEFAULT NULL COMMENT '消息首次发送时间，DRAFT状态时为NULL',
