  `money_password` varchar(225) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '交易密码',
  `show_money_password` varchar(225) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '交易密码(明码)',
  `source_host` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '资源服务器域名',
  `avatar` varchar(225) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '用户头像',
  `user_group` int DEFAULT '0' COMMENT '正常 0 假人 1 机器人 2',
  `frozen` bit(1) DEFAULT b'0' COMMENT '是否冻结 0否1是',
