  `bank_card_user_name` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT NULL COMMENT '持卡人姓名',
  `sec_contract_control` bit(1) DEFAULT b'0' COMMENT '开启秒合约控制 0否1是',
  `sec_contract_rate` decimal(5,2) DEFAULT '0.00' COMMENT '秒合约获胜概率',
  `buy_times` int DEFAULT '1' COMMENT '购买次数',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `acount_index` (`user_account`) USING BTREE COMMENT '用户账号唯一索引'
) ENGINE=InnoDB AUTO_INCREMENT=10135 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='用户表';

