DROP TABLE IF EXISTS `app_user_webhook_log`;
CREATE TABLE `app_user_webhook_log` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT 'id',
  `user_id` bigint NOT NULL COMMENT '会员id',
  `coin_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '资产符号（在钱包中对应哪种资产）',
  `network` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '区块链网络 trc20 erc20',
  `address` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '钱包地址',
  `txid` varchar(225) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '交易hash',
  `ts` bigint DEFAULT NULL COMMENT '交易时间',
  `direction` int NOT NULL DEFAULT '1' COMMENT '方向 （1：转入，0：转出）',
  `from_address` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '来源地址',
  `to_address` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '目标地址',
  `symbol` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '通证名称 一般是 USDT',
  `token_decimal` int NOT NULL DEFAULT '6' COMMENT '精度  USDT的精度是6. 例如 1USDT 的值为 1000000  500000000 = 500 * 1000000',
  `reason` varchar(500) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT NULL COMMENT '失败原因,如果有',
  `ip` varchar(225) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'ip地址',
  `amount` decimal(24,6) DEFAULT '0.000000' COMMENT '数量',
  `status` int NOT NULL DEFAULT '0' COMMENT '状态 （0：等待结算，1:已结算）',
  `settlement_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '结算时间',
  `create_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='webhook记录';

