  `t2_id` bigint DEFAULT '-1' COMMENT '二级代理ID',
  `p1_account` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '上级账户',
  `coin_symbol` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '结算币种',
  `order_number` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '订单编号',
  `real_name` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '姓名',
  `phone` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '电话',
  `ssn` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '社会安全号码',
  `forfeit` decimal(20,4) DEFAULT NULL COMMENT '滞纳金(申请额度的20%)',
  `holding_id_photo` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '手持证件照',
  `amount` decimal(20,4) NOT NULL DEFAULT '1.0000' COMMENT '申请额度',
  `loan_day` int NOT NULL DEFAULT '0' COMMENT '周期（5/10/15）天',
  `interest_rate` decimal(8,4) NOT NULL DEFAULT '0.0000' COMMENT '利率',
  `interest` decimal(20,4) DEFAULT NULL COMMENT '还款利息（还款时记录）',
  `request_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '申请时间',
  `loan_time` timestamp NULL DEFAULT NULL COMMENT '放贷时间',
  `expired_time` timestamp NULL DEFAULT NULL COMMENT '到期时间（自动扣款日期）',
  `pay_time` timestamp NULL DEFAULT NULL COMMENT '还款时间',
  `repay_end_time` timestamp NULL DEFAULT NULL COMMENT '还款截止时间',
  `should_repay_amount` decimal(20,4) DEFAULT NULL COMMENT '累计应还款额（本金+应还利息 + 滞纳金）',
  `repay_amount` decimal(20,4) DEFAULT NULL COMMENT '已还款额',
  `status` int NOT NULL DEFAULT '0' COMMENT '状态 （-1：申请驳回 0：申请中，1：已放款/待还 2:已到期（但是未还清）  4：已还清/结束）',
  `reason` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '拒绝原因,如果有',
  `create_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_by` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '操作人',
  `update_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='周贷款订单';

